import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DWdSXOaE.js";const A=JSON.parse('{"title":"用户同步设计方案","description":"","frontmatter":{},"headers":[],"relativePath":"AI_docs/用户同步方案.md","filePath":"AI_docs/用户同步方案.md"}'),r={name:"AI_docs/用户同步方案.md"};function o(n,l,s,_,c,d){return e(),a("div",null,[...l[0]||(l[0]=[t('<h1 id="用户同步设计方案" tabindex="-1">用户同步设计方案 <a class="header-anchor" href="#用户同步设计方案" aria-label="Permalink to &quot;用户同步设计方案&quot;">​</a></h1><p>​ 用户同步数据是比较敏感的，传输中需要考虑安全性、性能和易用性。依据这三个特征采用非对称加密与对称加密结合方式，既能保存安全性，以能兼顾性能。</p><h3 id="加密方案概述" tabindex="-1">加密方案概述 <a class="header-anchor" href="#加密方案概述" aria-label="Permalink to &quot;加密方案概述&quot;">​</a></h3><ol><li>采用 &quot;非对称加密 + 对称加密&quot; 的混合模式</li><li>服务端生成 RSA 密钥对，客户端持有公钥</li><li>客户端请求时生成 AES 随机密钥，用 RSA 公钥加密后发送</li><li>服务端用 RSA 私钥解密得到 AES 密钥，用此密钥加密返回数据</li><li>客户端用本地 AES 密钥解密获取数据</li></ol><h3 id="加密方案说明" tabindex="-1">加密方案说明 <a class="header-anchor" href="#加密方案说明" aria-label="Permalink to &quot;加密方案说明&quot;">​</a></h3><ol><li><p><strong>安全性保障</strong>：</p><ul><li>采用 RSA 2048 位和 AES 256 位加密算法，满足高安全性要求</li><li>每次通信使用随机生成的 AES 密钥，避免密钥重复使用风险</li><li>应用启动生成密钥对，不对密钥存储，每次启动重新生成</li><li>添加请求时间戳和签名机制，防止重放攻击</li></ul></li><li><p><strong>实现流程</strong>：</p><ul><li>服务端启动时生成 RSA 密钥对，并提供公钥获取接口</li><li>客户端调用接口前获取首先获取服务端公钥</li><li>客户端请求数据时： <ul><li>生成随机 AES 密钥</li><li>用 RSA 公钥加密 AES 密钥</li><li>发送加密的 AES 密钥和请求参数</li></ul></li><li>服务端响应时： <ul><li>用 RSA 私钥解密得到 AES 密钥</li><li>用 AES 密钥加密用户数据并返回</li></ul></li><li>客户端用本地 AES 密钥解密获取数据</li></ul></li></ol><h3 id="接口地址" tabindex="-1">接口地址 <a class="header-anchor" href="#接口地址" aria-label="Permalink to &quot;接口地址&quot;">​</a></h3><p>测试环境：<a href="http://172.19.11.110/c-interface/doc.html" target="_blank" rel="noreferrer">统一接口平台</a></p>',8)])])}const u=i(r,[["render",o]]);export{A as __pageData,u as default};
